export const uploadPoiPhoto = <ThrowOnError extends boolean = false>(
  options: Options<UploadPoiPhotoData, ThrowOnError>
) =>
  (options.client ?? client).post<UploadPoiPhotoResponses, UploadPoiPhotoErrors, ThrowOnError>({
    ...formDataBodySerializer,
    security: [{ scheme: "bearer", type: "http" }],
    url: "/upload/poi-photo",
    ...options,
    headers: {
      "Content-Type": null,
      ...options.headers,
    },
  });
